# Clean unwanted tokens and whitespace
clean = re.sub(r"<\ÔΩúbegin‚ñÅof‚ñÅsentence\ÔΩú>", "", raw).strip()

# Parse the inner JSON safely
try:
    parsed = json.loads(clean)
except json.JSONDecodeError:
    parsed = {}

# Return as separate fields
output.append({
    "json": {
        "engagement_score": parsed.get("engagement_score"),
        "upsell_opportunity_score": parsed.get("upsell_opportunity_score"),
