// Get both data sources
const leads = $items("Full Leads");    // node holding full lead objects
const mails = $items("Custom Mails");  // node holding {id, custom_mail}

// Build lookup by id
const mailMap = {};
mails.forEach(m => {
  mailMap[m.json.id] = m.json.custom_mail;
});

// Build output for Google Sheets
return leads.map((l, index) => {
  const lead = l.json;
  return {
    json: {
      rowNumber: index + 2,  // assumes header row is row 1
      id: lead.id,           // keep ID in case you need it
      email: lead.payload?.email || "",
      firstName: lead.payload?.firstName || "",
      lastName: lead.payload?.lastName || "",
      company: lead.payload?.companyName || "",
      CustomMail: mailMap[lead.id] || ""   // üëà must match EXACT column name in Google Sheet
    }
  };
});
